const numbers = [1, 2, 3, 4, 5]
const doubled = numbers.map(function(num) {
  return num * 2
})
console.log(doubled) // [2, 4, 6, 8, 10]

const set = new Set([1, 2, 3, 4, 5])
const doubledSet = Array.from(set).map(num => num * 2)
console.log(doubledSet) // [2, 4, 6, 8, 10]

