// Strings
const str = "abc"

for(key of str){
  console.log(key) // "a", "b", "c"
}

// Arrays
const arr = ['a', 'b', 'c']

for(value of arr){
  console.log(value) // "a", "b", "c"
}

// Maps
const map = new Map(); map.set(0, 'a'); map.set(1, 'b')

for(key of map){
  console.log(key) // [0,"a"], [1,"b"]
}

// Sets
const set = new Set(); set.add('a'); set.add('b')

for(key of set){
  console.log(key) // "a", "b"
}

const obj = {
  name: "Jack",
  age: 30,
  occupation: "Physician"
}

for (const key of Object.keys(obj)){
  console.log("key: "+ key+ ", value: "+ obj[key])
  // note that obj.key would return undefined
}
/* Output:
  "key: name, value: Jack"
  "key: age, value: 30"
  "key: occupation, value: Physician"
*/

for (const value of Object.values(obj)){
  console.log("value: "+ value)
}
/* Output:
  "value: Jack"
  "value: 30"
  "value: Physician"
*/

for (const entry of Object.entries(obj)){
  console.log(entry)
}
/* Output:
  ["name","Jack"]
  ["age",30]
  ["occupation","Physician"]
*/

// Strings
const str = "abc"

for(key in str){
  console.log(key, str[key])
}

// Arrays
const arr = ["a", "b", "c"]

for(key in arr){
  console.log(key, arr[key])
}

// Objects
const obj = {0: "a", 1: "b", 2: "c"}

for(key in obj){
  console.log(key, obj[key])
}

/* Outputs of each loop:
  "0" "a"
  "1" "b"
  "2" "c"
*/
