let a = 10
switch (a) {
  case (a >= 10):
    console.log('a is greater or equal than 10')
    break
  case (a < 10):
    console.log('a is less than 10')
}

//is the same as:
switch (a) {
  case (true):
    console.log('a is greater or equal than 10')
    break
  case (false):
